            asModel.ItemCd = ((OrderModel)value).ItemCd;
            asModel.ItemNm = ((OrderModel)value).ItemNm;
            asModel.FactBizUnit = ((OrderModel)value).FactBizUnit;
            asModel.CustomerPhone = ((OrderModel)value).PhoneNo;
        }
    }
