    [CascadingParameter] BlazoredModalInstance ModalInstance { get; set; }
    [Parameter] public ItemModel selectedPDItem { get; set; }
    [Parameter] public string loginId { get; set; }

    public GMINWEB_CJC.Pages.Common.ConfirmBase DeleteConfirmation { get; set; }


    private List<PDItemImageModel> pdItemImageModels;
    public PDItemImageModel selectedFile { get; set; }

    RadzenDataGrid<PDItemImageModel> detailGrid;
    private List<CommonCodeModel> FileClsNmList;

    private PDItemImageModel fileModelToInsert;
    private PDItemImageModel fileModelToDelete;

    string imgSrc = "";
    IFileListEntry file = null;
    byte[] _fileBytes = null;

    protected override async Task OnInitializedAsync()
    {
        if (selectedPDItem is not null)
        {
            pdItemImageModels = await _imageManager.GetPDItemImage(selectedPDItem);
        }

        FileClsNmList = await _codeManager.FindCommonCodeFromName("제품관련파일", loginId);

    }

    void OnChangeDetailCls(object value, string name)
    {
        if (value is not null && fileModelToInsert is not null)
        {
            fileModelToInsert.FileCls = ((CommonCodeModel)value).CodeSeq;
            fileModelToInsert.FileClsNm = ((CommonCodeModel)value).CodeSubNm;
        }
    }

    void btnCancleClick()
    {
        ModalInstance.CancelAsync();
    }

    async Task InsertRow()
    {
        if (selectedPDItem is not null)
        {
            fileModelToInsert = new PDItemImageModel();
            fileModelToInsert.BizUnit = selectedPDItem.BizUnit;
            fileModelToInsert.FactBizUnit = selectedPDItem.FactBizUnit;
            fileModelToInsert.ItemCd = selectedPDItem.ItemCd;

            await detailGrid.InsertRow(fileModelToInsert);
        }
        else
        {
            await jsRuntime.InvokeVoidAsync("alert", "제품 관련 파일 업로드 오류");
        }
    }

    async Task EditRow(PDItemImageModel file)
    {
        fileModelToInsert = file;

        await detailGrid.EditRow(file);
    }

    async Task SaveRow(PDItemImageModel file)
    {
        if (file == fileModelToInsert)
        {
            fileModelToInsert = null;
        }

        if (ValidationCheckFile(file) == true)
        {
            if (file.FileNo == 0)
            {
                //파일넘버가 채번되기 전이면 INSERT
                var result = await _imageManager.InsertData(file, loginId);
                if (result.IsError())
                {
                    await jsRuntime.InvokeVoidAsync("alert", result.GetErrorMessages());
                }
            }
            else
            {
                //파일넘버가 기존에 존재하면 UPDATE
                var result = await _imageManager.UpdateData(file, loginId);
                if (result.IsError())
                {
                    await jsRuntime.InvokeVoidAsync("alert", result.GetErrorMessages());
                }
            }

            await detailGrid.UpdateRow(file);
            pdItemImageModels = await _imageManager.GetPDItemImage(selectedPDItem);
        }
        else
        {
            await jsRuntime.InvokeVoidAsync("alert", "파일 종류와 파일을 선택하여 주십시오.");
        }
    }

    void CancelEdit(PDItemImageModel file)
    {
        if (file == fileModelToInsert)
        {
            fileModelToInsert = null;
        }

        detailGrid.CancelEditRow(file);
    }

    async Task DeleteRow(PDItemImageModel file)
    {
        if (file == fileModelToInsert)
        {
            fileModelToInsert = null;
        }

        //저장 후 삭제일 때 DB 에서 DELETE
        if (pdItemImageModels.Contains(file))
        {
            selectedFile = file;
            DeleteConfirmation.Show("삭제하시겠습니까?");
        }
        //저장 전 삭제일 때 GRID UI에서만 삭제
        else
        {
            detailGrid.CancelEditRow(file);
            await detailGrid.Reload();
        }
    }

    public async Task HandleFileSelected(IFileListEntry[] files)
    {
        if (files.Count() > 0)
        {
            file = files.FirstOrDefault();
            using (var ms = new MemoryStream())
            {
                await file.Data.CopyToAsync(ms);
                _fileBytes = ms.ToArray();

                fileModelToInsert.FileNm = file.Name;
                fileModelToInsert.FileType = file.Type;
                fileModelToInsert.FileData = Convert.ToBase64String(_fileBytes);
            }
        }
    }


    void btnDownloadFile(PDItemImageModel file)
    {
        var fileArr = Convert.FromBase64String(file.FileData);
        jsRuntime.InvokeVoidAsync("DownloadAsFile", file.FileNm, file.FileType, fileArr);
    }

    void ShowNotification(NotificationMessage message)
    {
        NotificationService.Notify(message);
    }

    protected async Task ConfirmDelete_Click(bool deleteConfirmed)
    {
        if (deleteConfirmed)
        {
            var result =  await _imageManager.DeleteData(selectedFile, loginId);

            if (result.IsError())
            {
                await jsRuntime.InvokeVoidAsync("alert", result.GetErrorMessages());
            }
            else
            {
                pdItemImageModels = await _imageManager.GetPDItemImage(selectedPDItem);
                await detailGrid.Reload();
            }

        }
        else
        {
            selectedFile = null;
        }
    }

    bool ValidationCheckFile(PDItemImageModel file)
    {
        //필수값 미입력시 false 반환
        if (file.FileNm is null || file.FileData is null || file.FileType is null)
        {
            return false;
        }
        else
        {
            return true;
        }
    }

